import { createTheme } from '@mui/material/styles';
import {  grey, blue, deepOrange} from '@mui/material/colors';


const getDesign = (mode) => ({
    palette: {
      mode,
      ...(mode === 'light'
        ? {
            // Valores da paleta para o modo claro
            primary: deepOrange, //btn mouse pass em cima
            divider: deepOrange[900],
            text: {
              primary: grey[900], //nome input
              secondary: grey[900],
            },
          }
        : {
            // Valores da paleta para o modo escuro
            primary: grey,
            divider: grey[50],
            background: {
              default: grey[900],
              
            },
            text: {
              primary: '#fafafa',
              secondary: grey[500],
            },
          }),
    },
  });
  
  export default getDesign;
