import { createTheme } from '@mui/material/styles';
import {  blue, grey,  orange} from '@mui/material/colors';
import { Box } from '@mui/material';


const getDesign = (mode) => ({
    accent: orange['A400'],
    accent: blue['A400'],
    palette: {
      mode,
      ...(mode === 'light'
        ? {
            // Valores da paleta para o modo claro
            primary: orange, //btn mouse pass em cima
            divider: orange[800],
            text: {
              primary: grey[800], //nome input
              secondary: grey[700],
            },
          }
        : {
            // Valores da paleta para o modo escuro
            primary: grey,
            divider: grey[900],
            background: {
              default: grey[900],
         
            },
            text: {
              primary: '#fafafa',
              secondary: grey[800],
            },
          }),
    },
  });
  
  export default getDesign;
