import { createTheme } from '@mui/material/styles';
import {   grey,  orange} from '@mui/material/colors';


const getDesign = (mode) => ({
    palette: {
        mode,
        ...(mode === 'light'
          ? {
              primary: orange,
              divider: orange[800],
              text: {
                primary: grey[800],
                secondary: grey[700],
              },
              background: {
                default: '#fff',
              },
            }
          : {
              primary: grey,
              divider: grey[50],
              background: {
                default: '#000', // Preto para o modo escuro
              },
              text: {
                primary: '#fafafa',
                secondary: grey[500],
              },
            }),
      },
    });
  
  export default getDesign;
