import React, { useState } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import Button from '@mui/material/Button';
import getDesign from './Theme';
import Formulario from './Formulario';
import Sheet from '@mui/joy/Sheet';



const App = () => {
  const [mode, setMode] = useState('light');

  // Cria o tema com base no modo atual
  const theme = React.useMemo(() => createTheme(getDesign(mode)), [mode]);

  // Alterna entre modo claro e escuro
  const handleModeChange = () => {
    setMode((prevMode) => (prevMode === 'light' ? 'dark' : 'light'));
  };

  return (
    <ThemeProvider theme={theme}>
    <Sheet
        style={{
            height: '100vh',
            width: '100vw',
            padding: '2rem',
            backgroundColor: theme.palette.background.default,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
        }}
    >

        <Button variant="contained" onClick={handleModeChange}>
            Mudar para {mode === 'light' ? 'Escuro' : 'Claro'}
        </Button>
        <Formulario />
    </Sheet>
</ThemeProvider>
  );
};

export default App;
