import { orange, grey } from '@mui/material/colors';

// Função para gerar tokens de design com base no modo
const getDesign = (mode) => ({
  palette: {
    mode,
    ...(mode === 'light'
      ? {
          // Valores da paleta para o modo claro
          primary: orange,
          divider: orange[800],
          text: {
            primary: grey[800],
            secondary: grey[700],
          },
          background: {
            default: mode === 'dark' ? '#000000' : '#ffffff',
          },
        }
      : {
          // Valores da paleta para o modo escuro
          primary: grey,
          divider: grey[800],
          background: {
            default: grey[900], // Preto para o modo escuro
          },
          text: {
            primary: '#121212',
            secondary: grey[800],
          },
        }),
  },
});

export default getDesign;
