import React, { useState } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import Button from '@mui/material/Button';
import getDesign from './Theme';
import Formulario from './Formulario';



const App = () => {
  const [mode, setMode] = useState('light');

  // Cria o tema com base no modo atual
  const theme = React.useMemo(() => createTheme(getDesign(mode)), [mode]);

  // Alterna entre modo claro e escuro
  const handleModeChange = () => {
    setMode((prevMode) => (prevMode === 'light' ? 'dark' : 'light'));
  };

  return (
    <ThemeProvider theme={theme}>
  <Paper
        sx={{ 
          backgroundcolor: 'background.default', // Aplica a cor de fundo do tema
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          padding: '2rem',
          margin: 0,
          overflow: 'hidden',
        }}
      >
        <Button variant="contained" onClick={handleModeChange}>
          Mudar para {mode === 'light' ? 'Escuro' : 'Claro'}
        </Button>
        <Formulario />
      </Paper>
    </ThemeProvider>
  );
};

export default App;
