import React from 'react';
import { FormControl, FormLabel, TextField, FormHelperText, Typography, Paper, Button } from '@mui/material';
import PersonIcon from '@mui/icons-material/Person';
import EmailIcon from '@mui/icons-material/Email';
import LockIcon from '@mui/icons-material/Lock';

const inputCadastro = (label, name, type = "text", icon, placeholder, error) => (
    <FormControl style={{ marginBottom: '1rem' }}>
        <FormLabel>{label}</FormLabel>
        <TextField
            name={name}
            type={type}
            value={form[name]}
            onChange={handleChange}
            onFocus={() => setShowInfo(name === 'senha')}
            onBlur={() => setShowInfo(false)}
            error={Boolean(error)}
            InputProps={{
                startAdornment: (
                    <InputAdornment position="start" sx={{ color: 'primary.main', mr: 1 }}>
                        {icon}
                    </InputAdornment>
                ),
            }}
            placeholder={placeholder}
            variant="outlined"
            fullWidth
            helperText={error}
        />
        {name === 'senha' && showInfo && (
            <>
                <FormHelperText style={{ fontSize: '0.75rem' }}>
                    - Senha deve ter no mínimo 8 caracteres.
                </FormHelperText>
                <FormHelperText style={{ fontSize: '0.75rem' }}>
                    - Conter pelo menos 1 letra minúscula e maiúscula.
                </FormHelperText>
                <FormHelperText style={{ fontSize: '0.75rem' }}>
                    - Conter pelo menos 1 número.
                </FormHelperText>
            </>
        )}
    </FormControl>
);

const Formulario = ({ errors, form, handleChange, handleSubmit, isSubmitting, showInfo, setShowInfo }) => (
    <Paper
        elevation={3}
        component="form"
        onSubmit={handleSubmit}
        style={{
            display: 'flex',
            flexDirection: 'column',
            gap: '1rem',
            maxWidth: '400px',
            margin: '0 auto',
            marginTop: '10%',
            padding: '2rem',
            backgroundColor: '#fff'
        }}
    >
        <Typography variant="h4" component="h1" style={{ textAlign: 'center' }}>
            Formulário de Cadastro
        </Typography>

        {inputCadastro('Nome', 'nome', 'text', <PersonIcon sx={{ color: 'blue' }} />, 'Nome', errors.nome)}
        {inputCadastro('E-mail', 'email', 'email', <EmailIcon sx={{ color: 'green' }} />, 'Digite seu e-mail', errors.email)}
        {inputCadastro('Senha', 'senha', 'password', <LockIcon sx={{ color: 'red' }} />, '********', errors.senha)}
        {inputCadastro('Confirmação de Senha', 'confirmacaoSenha', 'password', <LockIcon sx={{ color: 'red' }} />, '********', errors.confirmacaoSenha)}

        <Button type="submit" disabled={isSubmitting} variant="contained" color="primary">
            Cadastrar
        </Button>
    </Paper>
);

export default Formulario;
