// src/App.jsx

import React, { useState } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import Typography from '@mui/material/Typography';
import getDesign from './Theme';
import Formulario from './Formulario';

const App = () => {
  const [mode, setMode] = React.useState('light');

  const theme = React.useMemo(() => createTheme(getDesign(mode)), [mode]);

  // Alterna entre modo claro e escuro
  const handleModeChange = () => {
    setMode((prevMode) => (prevMode === 'light' ? 'dark' : 'light'));
  };

  return (
    <ThemeProvider theme={theme}>
      <Box
        sx={{
          minHeight: '100vh',
          minWidth: '100vw',
          bgcolor: 'background.default', // Aplica a cor de fundo do tema
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          padding: '2rem',
          margin: 0,
        }}
      >
        <Typography variant="h4" component="h1" sx={{ mb: '1rem' }}>
          Alternar Modo
        </Typography>
        <FormControlLabel
          control={
            <Switch
              checked={mode === 'dark'}
              onChange={handleModeChange}
              color="primary"
            />
          }
          label={mode === 'light' ? 'Modo Escuro' : 'Modo Claro'}
        />
        <Formulario />
      </Box>
    </ThemeProvider>
  );
};

export default App;
